#-*- coding:utf-8 -*-
import sys

def asciify(src, char_width, ascii_map):
    """
    Return an ASCII representation of the string src,
    based on the passed ascii_map.
    ascii_map should be a multiline string representing
    the whole alphabet in ASCII art.
    Parameter char_width should be the width of a single
    ASCII letter, in characters.
    """
    ascii_map = ascii_map.split('\n')
    ascii_str = []
    for l in ascii_map:
        line = []
        for c in src.upper():
            if ord('A') <= ord(c) and ord(c) <= ord('Z'):
                i = ord(c) - ord('A')
            else:
                i = ord('Z') + 1 - ord('A')
            for j in range(i*char_width, (i*char_width)+char_width):
                # sys.stdout.write(l[j])
                line.append(l[j])
        # sys.stdout.write('\n')
        ascii_str.append("".join(line))
    return "\n".join(ascii_str)

